            // CF :
            $optionCf = "";
            if (isset($formCf) && !empty($formCf)) {
                $optionCf = implode('
', $formCf);
            }

            // EMAIL :
            $sendEmail = wp_mail(
                $contact[0]->contact_form_setting_notification_email,

                // SUBJECT : [NOM SITE] "Opportunité" ou "Prenom NOM prospect" :
                $emailSubject,

